import React from 'react'
import styled from "styled-components";


const ProductCard = () => {
    return (

    <Card className="card">
        <div className="card-body">
            <Title className="card-title">Samsung S20</Title>
            <Description className="card-text">
                Price: Rs 1200 
            </Description>
            <ActionButton href="#" class="btn btn-primary">Add to Cart</ActionButton>
        </div>
    </Card>

    )
}
const Card = styled.div`
color:	blue;
width: 18rem;
padding-top:20px;
`
const Title = styled.h2`
  color: 000000;
  font-weight: 300;
`

const Description = styled.p`
  color: 000000;
  font-weight: 500;
`
const ActionButton = styled.button`
  margin: 0 5px;
  padding: 8px 14px;
  background: lightblue;
  color: blue;
  cursor: pointer;
  border: 1px solid #fff;
  outline: 0;
  font-weight: 300;
  :hover {
    opacity: 0.8;
  }
`
export default ProductCard;
