import React, { Component } from 'react';
import "./NavBar.css";
import { Link } from "react-router-dom"
import logo from "./../../logo.png";
import cart from "./../../cart.svg";
import styled from "styled-components"

class Navbar extends Component {
    
    render() {
        return (
            
            <div className="container">

                {/* a light navbar that is responsive for large devices */}
                <nav className="navbar navbar-light navbar-expand-lg bg-light" role="navigation" >

                    <ul className="navbar-nav mr-auto">
                        <img src={logo} height="50" atl="logo" />
                        <a href="#" className="navbar-brand"><Link to="/">Cool Brand</Link></a>
                    </ul>

                    {/* center nav link products and mycarts */}
                    <div className="mx-auto order-0">
                        <a href="#" className="navbar-brand mx-auto">

                            <span className="p-4">Phones</span>
                        </a>
                    </div>
                    <div className="mx-auto order-0">
                        <a href="#" className="navbar-brand mx-auto">

                            <span className="p-4"><img src={cart} height="40" atl="logo" />MyCart</span>
                        </a>

                    </div>


                    {/* login and sign up button */}
                    <div class="navbar-collapse collapse w-100 order-3 dual-collapse2">
                        <ul class="navbar-nav ml-auto">
                            <li class="nav-item">
                                <a class="nav-link" href="#">
                                    <ButtonContainer>login</ButtonContainer>
                                       
                                </a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">
                                <ButtonContainer>Sign up</ButtonContainer>
                                 </a>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
        )
    }
}

export default Navbar;

const ButtonContainer = styled.button`
 background:blue;
 text-transform: capitalize;
 margin : 0.2rem 0.5rem 0.2rem 0;
 cursor:pointer;
 transition:all 0.4s ease-in-out;
 &:hover{
     background:var(--lightBlue);
     color:var(--mainBlue)
 }

`
