import React, { Component } from 'react';
import { Link } from "react-router-dom"
import styled from "styled-components";

import "./NavBar.css";
import logo from "./../../logo.png";
import cart from "./../../cart.svg";
import user from "./../../user.svg";
import {ButtonContainer} from "./../Styled/Button";


class Navbar extends Component {
    
    render() {
        return (
            
            <div className="container">

                {/* a light navbar that is responsive for large devices */}
                <NavWrapper className="navbar navbar-light navbar-expand-lg bg-light" style={{color:blue}} role="navigation" >

                    <ul className="navbar-nav mr-auto align-items-center">
                        <img src={logo} height="50" alt="logo" />
                        <div  className="navbar-brand">
                            <Link to="/">CoolBrand</Link>
                        </div>
                    </ul>

                    {/* center nav link products and mycarts */}
                    <div className="mx-auto order-0">
                        <a  className="navbar-brand mx-auto">
                        <Link to="/product">Phones</Link>
                        </a>
                    </div>

                    <div className="mx-auto order-0">
                        <a href="#" className="navbar-brand mx-auto">
                            <span className="p-4">
                                <img src={cart} height="40" atl="logo" />
                                <Link to="/mycart">MyCart</Link>
                                </span>
                        </a>

                    </div>


                    {/* login and sign up button */}
                    <div class="navbar-collapse collapse w-100 order-3 dual-collapse2">
                        <ul class="navbar-nav ml-auto">
                            <li class="nav-item">
                                <a class="nav-link" href="#">
                                    <ButtonContainer>
                                     <img src={user} height="25" atl="user" />
                                        login
                                     </ButtonContainer>
                                       
                                </a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">
                                <ButtonContainer>Sign up</ButtonContainer>
                                 </a>
                            </li>
                        </ul>
                    </div>
                </NavWrapper>
            </div>
        )
    }
}

export default Navbar;


const NavWrapper = styled.nav`

 background-color :var(--mainBlue);
 .navlink{
     color:var(Blue);
     font-size:1.3rem;
     text-transform: capitalize;
 }

`
